const SavedAppointments = require("../schema/SavedAppointments");
const Image = require("../customTools/image");
const Hash = require("../customTools/hash");
const exportFunction = module.exports = {};


exportFunction.getSavedAppointments = async(customerUserName)=>{
    saved = await SavedAppointments.find({"customerUserName" : customerUserName});
    return saved;
}

exportFunction.findTemplateByID = async(templateID) =>{
    saved = await SavedAppointments.findOne({"_id" : templateID});
    return saved;
}

exportFunction.saveAppointment = async(
    appointmentName, stylistID, serviceList, 
    serviceIDList, appointmentNotes, imageString, 
    userName, currentCustomer, stylistName, appointmentPic, timeSlots
) =>{

    docID = await Hash.getObjectId();
    
    let blankCanvas = 'data:image/png;base64,';
    if(appointmentPic === blankCanvas){
        appointmentPic = "/icon/default.webp";
    }
    else{
        appointmentPic = await Image.saveTemplateImage(appointmentPic, docID, userName);
    }

    formatedServiceList = serviceList.split(",");
    formatedServiceIDList = serviceIDList.split(",");


    SavedAppointments.create({
        "_id" : docID,
        "customerID" : currentCustomer._id,
        "customerName" : `${currentCustomer.firstName} ${currentCustomer.lastName}`,
        "customerUserName" : `${userName}`,
        "appointmentName" : appointmentName, 
        "stylistID" : stylistID, 
        "serviceList" : formatedServiceList, 
        "serviceIDList" : formatedServiceIDList, 
        "notes" : appointmentNotes, 
        "servicePic" : imageString,
        "stylistName" : stylistName,
        "appointmentPic" : appointmentPic.relateivePath,
        "timeSlots" : Number(timeSlots)
    });
}


exportFunction.editTemplate = async(templateID, appointmentNameEdit, serviceList, serviceIDList, appointmentNotes, appointmentImage, userName)=>{

    let blankCanvas = 'data:image/png;base64,';
    if(appointmentImage === blankCanvas){
        await SavedAppointments.updateOne(
            { "_id" : templateID },
            {
                "appointmentName" : appointmentNameEdit,
                "serviceList" : serviceList,
                "serviceIDList" : serviceIDList,
                "notes" : appointmentNotes
            }
        );
    }
    else{
       appointmentPic = await Image.saveTemplateImage(appointmentImage, templateID, userName);
        await SavedAppointments.updateOne(
            { "_id" : templateID },
            {
                "appointmentName" : appointmentNameEdit,
                "serviceList" : serviceList,
                "serviceIDList" : serviceIDList,
                "notes" : appointmentNotes,
                "appointmentPic" : appointmentPic.relativePath
            }
        );
    }
}

exportFunction.deleteTemplate = async(templateID) => {
    await SavedAppointments.deleteOne({"_id" : templateID});
}